sys_navigator.script:
var url = '';
var sysid = g_uri.get('sys_id');
var isCompatible = sn_sm.SMTemplateCompatibility.getCompatibility();
if (isCompatible == 'block') {
	url =  g_uri.toString(); 
}
else {
   g_uri.set('sysparm_sys_id', sysid);
   g_uri.set('sysparm_record_target','x_152362_crisisnow_request_template');
   g_uri.set('sysparm_title','CrisisNow Request Template');
   g_uri.set('sysparm_app_name','crisisnow');
   url =  g_uri.toString('$sm_templates.do');
}
url;
