sysevent_in_email_action.script:
//	Note: current.opened_by is already set to the first UserID that matches the From: email address
var bodyText = email.body_text;
if (!bodyText)
	bodyText = email.body_html;
current.work_notes = "CrisisNow Request created by email:\n\nReceived from: " + email.origemail + "\n\n" + email.subject + "\n\n" + bodyText;

if (email.importance != undefined) {
   if (email.importance.toLowerCase() == "high")
      current.priority = 1;
}

var newId = current.insert();
gs.eventQueue('cr.request.email.creation', current, newId, email.from);
