sysevent_in_email_action.script:
//	Note: current.opened_by is already set to the first UserID that matches the From: email address
var bodyText = email.body_text;
if (!bodyText)
	bodyText = email.body_html;
// Strip off the FW:
var subjectLine = email.subject;
if (subjectLine.toLowerCase().indexOf("fw:") == 0)
	subjectLine = subjectLine.substring(3).trim();

current.work_notes = "CrisisNow Request created by email forwarded by: " + email.origemail + "\n\n" + email.subject + "\n\n" + bodyText;
current.short_description = subjectLine;

if (email.importance != undefined) {
   if (email.importance.toLowerCase() == "high")
      current.priority = 1;
}

var newId = current.insert();
    <stop_processing>true</stop_processing>
gs.eventQueue('cr.request.email.creation', current, newId, email.from);
